import React from 'react'

import { useState } from 'react'

const AddConstituency = ({ onAdd}) => {
  const [county_name, setName] = useState('')
  const [county_code, setCode] = useState('')
  const onSubmit = (e) => {
    e.preventDefault()

    if (!county_name) {
      alert('County code missing!');
      return;
    }else if (!county_code){
      alert('County name missing ');
      return;
    }

    onAdd({ county_name: county_name, county_code: county_code })
    setName('')
    setCode('')
  }

  return (
     <form className='add-form' onSubmit={onSubmit}>
      <div className='form-control'>
        <label>County</label>

        <select>
    <option value={county_code}  >Test</option>

        </select>
        <input
          type='number' maxLength={3}
          placeholder='County code'
          value={county_code}
          onChange={(e) => setCode(e.target.value)}
        />
      </div>
      <div className='form-control'>
        <label>Constituency code</label>
        <input
          type='text'
          placeholder='County name'
          value={county_name}
          onChange={(e) => setName(e.target.value.toUpperCase())}
        />
      </div>
      <div className='form-control'>
        <label>Consituency name </label>
        <input
          type='text'
          placeholder='County name'
          value={county_name}
          onChange={(e) => setName(e.target.value.toUpperCase())}
        />
      </div>
      <button type='submit'  className='btn-submit' >Add Record</button>
    </form>
  )
}

export default AddConstituency
